mapper.select:


	 and compId = #{compId} 
	 and compName = #{compName} 
	 and compIndustry = #{compIndustry} 
	 and compSize = #{compSize} 
	 and compAddress = #{compAddress} 
	 and complink = #{complink} 
	 and creditcode = #{creditcode} 
	 and compIntro = #{compIntro} 
	 and approveStatus = #{approveStatus} 

order by

	 compId desc 
	 compName desc 
	 compIndustry desc 
	 compSize desc 
	 compAddress desc 
	 creditcode desc 
	 compEsDate desc 
	 approveStatus desc 
	 compId desc
